import re
import itertools
import pandas as pd

# find all overlapping patterns in the string
def pattern_count(text, pattern):
    pattern_regex = '(?=' + pattern + ')'
    return(len(re.findall(pattern_regex, text)))

genome = ''
k = 11
L = 566
t = 18

def computing_frequencies(text, k):
    unique_letters = 'ACGT'
    x = itertools.product(unique_letters, repeat = k)
    kmer = ([''.join(i) for i in x])
    pattern_list = []    
    for i in range(0, len(kmer)):
        pattern = kmer[i]
        count = pattern_count(text, pattern)
        pattern_list.append(dict([('pattern',pattern), ('count', count)]))
    #frequencies = [ i['count'] for i in pattern_list ]
    #out = ' '.join(map(str,frequencies))
    count_df = pd.DataFrame(pattern_list)
    return(count_df)
    #print(out)


def clump_finding(genome, k, L, t):
    fw = pd.DataFrame(columns = ['i','pattern', 'count'])
    fw_out = pd.DataFrame(columns = ['i', 'pattern', 'count'])

    for i in range(0, len(genome) - L):
        clump = genome[i:i+L]
        fw_temp = computing_frequencies(clump, k)
        fw_temp['i'] = i
        fw = fw.append(fw_temp)

    fw_out = fw_out.append(fw[fw['count'] == t])
        #return(fw_out)
    fw_out = fw_out.drop_duplicates(subset = ['pattern', 'count'])
    print(fw_out)

