def hamming_distance(p, q):
    out = 0
    for i in range(0, len(p)):
        if p[i] != q[i]:
            out += 1
    return(out)


def approximate_pattern_matching(pattern, text, d):
    #frequency_array = [0] * 4**k
    approximate_pattern_indices = []
    pattern_length = len(pattern)
    for i in range(0, len(text) - pattern_length + 1):
        pattern_in_text = text[ i : i + pattern_length]
        hd_pattern = hamming_distance(pattern, pattern_in_text)
        if hd_pattern <= d:
            approximate_pattern_indices.append(i)
    
    print(' '.join(map(str,approximate_pattern_indices)))



pattern = 'AGGTACAT'
text = ''
d = 5



with open('C:/Users/Ainesh/Downloads/dataset_9_4 (2).txt') as f:
    file = f.read().splitlines()
    
pattern = file[0]
text = file[1]
d = int(file[2])


approximate_pattern_matching(pattern, text, d)